  'setting_event_view_time_stamp_profile' => '<title>Event Profile Time Stamp</title><info>Time stamp used when displaying events on a users profile.</info>',
  'setting_facebook_api_key' => '<title>Facebook API Key</title><info>Provide the API Key for your Facebook application.</info>',
  'setting_facebook_app_id' => '<title>Application ID</title><info>Provide the Application ID for your Facebook application.</info>',
  'setting_facebook_like_blog' => '<title>Enable Facebook Like Button (Blogs)</title><info>Enable to use the Facebook Like Button feature.</info>',
